void textout(string text) {
	double textnum = 0;

	while (text[textnum] != '\0')
	{
		cout << text[textnum];
		Sleep(75); //set to 75
		textnum++;
	}

}

void gameOver() {

	system("color 40");
	system("CLS");
	textout("You and your companions have fallen, do not feel bad for those who are your betters have gone before you and also fallen to the last. This world may end but fear not, you can try once more in a other world...");
	cin.ignore(1);
	exit(0);

}

